INSERT INTO Error_Check VALUES (1,'achan','Solution','Solution_Number','RegExp','[^0-9]+','0000-00-00',3,'Solution_Number that are not numbers'),(2,'achan','Plate','FK_Rack__ID','SQL','FK_Rack__ID is null or FK_Rack__ID = \'\' or FK_Rack__ID = 0','0000-00-00',3,'Plates that do not belong to any racks'),(4,'achan','Account','Account_Type','SQL','Account_Type is null or Account_Type = \'\'','2003-01-27',3,'Accounts that have no Account_Type'),(5,'achan','Box','Box_Type','SQL','Box_Type is null or Box_Type = \'\'','2003-01-27',3,'Boxes that have no Box_Type'),(6,'achan','VectorPrimer','FK_Vector__Name','SQL','FK_Vector__Name is null or FK_Vector__Name = \'\'','2003-01-27',3,'No reference to any vectors'),(7,'achan','Solution','Solution_Quantity,Quantity_Used','SQL','Quantity_Used > Solution_Quantity','2003-01-27',3,'Solutions with quantity used more than the total quantity'),(8,'achan','Solution,Stock','Solution_Type,Stock_Type','SQL','Stock_ID = FK_Stock__ID and ((Solution_Type = \'Reagent\' and Stock_Type = \'Solution\') or (Solution_Type = \'Solution\' and Stock_Type = \'Reagent\'))','0000-00-00',3,'Look for inconsistency between Stock_Type and Solution_Type'),(9,'achan','Sequence','FK_Sequence_Batch__ID','SQL','FK_Sequence_Batch__ID is null or  FK_Sequence_Batch__ID = \'\' or FK_Sequence_Batch__ID = 0','2003-01-27',3,''),(10,'achan','Clone_Sequence','FK_Sequence__ID','SQL','FK_Sequence__ID is null or  FK_Sequence__ID = \'\' or FK_Sequence__ID = 0','0000-00-00',3,''),(11,'achan','Sequence','Run_State','SQL','Run_State = \'Aborted\'','2003-01-27',3,''),(12,'achan','Sequence','Run_State','SQL','Run_State = \'Expired\'','2003-01-27',3,''),(13,'achan','Clone_Sequence','Sequence_Length','SQL','Sequence_Length < 2','2003-01-27',3,''),(14,'achan','Solution','FK_Stock__ID','SQL','FK_Stock__ID is null or FK_Stock__ID = \'\' or FK_Stock__ID = 0','2003-01-27',3,''),(16,'achan','Sequence,RunStats','Sequence_ID,RunStats_ID','FullSQL','select Sequence_ID,RunStats_ID from Sequence left join RunStats on Sequence_ID = FK_Sequence__ID where Run_State = \'Analyzed\' and RunStats_ID is null','2003-01-27',3,''),(17,'achan','Solution','Solution_Status,Quantity_Used','SQL','Solution_Status = \'Open\' and (Quantity_Used = 0 or Quantity_Used is null or Quantity_Used = \'\')','2003-01-27',3,''),(18,'achan','RunStats,Sequence,Plate','RunStats_ID,Sequence_ID,Plate_ID,NGs,No_Grows,No_Grows_Count','FullSQL','select RunStats_ID,Sequence_ID,Plate_ID,NGs,No_Grows,Length(No_Grows) - Length(Replace(No_Grows,\',\',\'\')) + 1 As No_Grows_Count from RunStats,Sequence,Plate where FK_Sequence__ID = Sequence_ID and FK_Plate__ID = Plate_ID and NGs > 0 and (NGs <> (Length(No_Grows) - Length(Replace(No_Grows,\',\',\'\')) + 1) or (No_Grows = \'\' or No_Grows is null)) order by RunStats_ID,Sequence_ID,Plate_ID,NGs','2003-01-27',3,'');
